let S = (selector) => document.querySelector(selector)

let newYears = '1 Jan 2022'
let formatTime = time => time < 10 ? `0${time}` : time

function countDown() {
    let newYearsDate = new Date(newYears)
    let currentDate = new Date()

    let totalSeconds = (newYearsDate - currentDate) / 1000

    let days = Math.floor(totalSeconds / 3600 / 24)
    let hours = Math.floor(totalSeconds / 3600) % 24
    let minutes = Math.floor(totalSeconds / 60) % 60
    let seconds = Math.floor(totalSeconds) % 60

    daysEl.innerHTML = days
    hoursEl.innerHTML = formatTime(hours)
    minsEl.innerHTML = formatTime(minutes)
    secondsEl.innerHTML = formatTime(seconds)
}

countDown()
setInterval(countDown, 1000)

function createStar() {
    let star = document.createElement('div')
    star.classList.add('star')
    star.style.left = Math.random() * 100 + 'vw'
    star.style.animationDuration = Math.random() * 2 + 3 + "s"
    star.innerText = '❆'
    star.style.padding = '1rem'
    document.body.appendChild(star)
    setTimeout(() => {
        star.remove()
    }, 5000)
    star.onclick = () => {
        star.style.opacity = '0'
    }
}

